CREATE TABLE [DestinationDB].[dbo].[raw_tb](
    LRID int IDENTITY (1,1),
	[Mid] [int] NULL,
	[ctype] [int] NOT NULL,
	[cvalue] [real] NULL,
	[tstamp] [datetime] NULL
) ON [PRIMARY] ; 
CREATE CLUSTERED INDEX CIX_LRID ON [DestinationDB].dbo.raw_tb (LRID);

CREATE TABLE [DestinationDB].[dbo].[cleandata_tb](
	[LRID] [int] IDENTITY(1,1) NOT NULL,
	[Machine] [varchar](10) NOT NULL,
	[Measurement] [varchar](14) NOT NULL,
	[Value] [real] NULL,
	[Timestamp] [datetime] NULL
) ON [PRIMARY]; 

CREATE CLUSTERED COLUMNSTORE INDEX CSIX_lrid ON [DestinationDB].dbo.cleandata_tb ;

CREATE TABLE [DestinationDB].dbo.lookupvalues_tb
(
    lid int IDENTITY(1,1),
    CodeId int,
    CodeName varchar(50),
    CodeType varchar(50),
    [description] varchar(500)
);

